mapper.select:
select id
from users
where id=#{inputId}

select pwd
from users
where id=#{inputId}

select id, pwd, email, regdate, profile
from users
where id=#{id}

mapper.update:
update users
set profile=#{profile}
where id=#{id}

update users
set pwd=#{pwd}
where id=#{id}

update users
set email=#{email}
where id=#{id}
